export const class11Courses = allCourses.filter(c => c.classLevel === 11);
export const class12Courses = allCourses.filter(c => c.classLevel === 12);
export const specialCourses = allCourses.filter(c => c.classLevel === 0);

export const formatDuration = (seconds: number): string => {
  const hours = Math.floor(seconds / 3600);
  const mins = Math.floor((seconds % 3600) / 60);
  if (hours > 0) return `${hours}h ${mins}m`;
  return `${mins} min`;
};

export const formatViews = (views: number): string => {
  if (views >= 1000000) return `${(views / 1000000).toFixed(1)}M`;
  if (views >= 1000) return `${(views / 1000).toFixed(1)}K`;
  return views.toString();
};

export const getTotalVideos = (): number => allCourses.reduce((sum, c) => sum + c.videos.length, 0);
export const getTotalDuration = (): number => allCourses.reduce((sum, c) => sum + c.videos.reduce((s, v) => s + v.duration, 0), 0);
